// src/components/TimeLine.js

import React from 'react';
import './TimeLine.css';
import { ReactComponent as WorkIcon } from '../assets/img/work.svg';
import { ReactComponent as SchoolIcon } from '../assets/img/school.svg';
import timelineElements from './timelineElements';
import { VerticalTimeline, VerticalTimelineElement } from 'react-vertical-timeline-component';
import 'react-vertical-timeline-component/style.min.css';

const TimeLine = () => {
  let workIconStyles = { background: "#06D6A0" };
  let schoolIconStyles = { background: "#f9c74f" };

  // Sort timeline elements by date
  const sortedTimelineElements = timelineElements.sort((a, b) => {
    return new Date(a.date) - new Date(b.date);
  });

  // Format date to "MMMM YYYY"
  const formatDate = (dateString) => {
    const options = { year: 'numeric', month: 'long' };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };

  return (
    <div className="timeline-container">
      <h2 className="title">Timeline</h2>
      <VerticalTimeline>
        {sortedTimelineElements.map((element) => {
          let isWorkIcon = element.icon === "work";
          let showButton = element.buttonText !== undefined && element.buttonText !== null && element.buttonText !== "";

          return (
            <VerticalTimelineElement
              key={element.id}
              date={formatDate(element.date)}
              dateClassName="date"
              iconStyle={isWorkIcon ? workIconStyles : schoolIconStyles}
              icon={isWorkIcon ? <WorkIcon /> : <SchoolIcon />}
            >
              <h3 className="vertical-timeline-element-title">
                {element.title}
              </h3>
              <h5 className="vertical-timeline-element-subtitle">
                {element.location}
              </h5>
              <p id="description">{element.description}</p>
              {showButton && (
                <a
                  className={`button ${isWorkIcon ? "workButton" : "schoolButton"}`}
                  href="/"
                >
                  {element.buttonText}
                </a>
              )}
            </VerticalTimelineElement>
          );
        })}
      </VerticalTimeline>
    </div>
  );
};

export default TimeLine;
